import React, { useState } from 'react';
import axios from 'axios';

const Chat = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { sender: 'user', text: input };
    setMessages((prev) => [...prev, userMessage]);
    setLoading(true);

    try {
      const response = await axios.post('http://localhost:3001/api/chat', {
        message: input
      });

      const botReply = JSON.stringify(response.data.result || response.data, null, 2);
      const botMessage = { sender: 'bot', text: botReply };

      setMessages((prev) => [...prev, botMessage]);
    } catch (err) {
      const errorMsg = { sender: 'bot', text: 'Error: ' + err.message };
      setMessages((prev) => [...prev, errorMsg]);
    }

    setLoading(false);
    setInput('');
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') sendMessage();
  };

  const clearChat = () => {
    setMessages([]);
  };

  return (
    <div style={styles.container}>
      <h2 style={{ textAlign: 'center' }}></h2>
      <button onClick={clearChat} style={styles.clearButton}>ðŸ§¹ Clear Chat</button>

      <div style={styles.chatBox}>
        {messages.map((msg, index) => (
          <div
            key={index}
            style={{
              ...styles.message,
              alignSelf: msg.sender === 'user' ? 'flex-end' : 'flex-start',
              backgroundColor: msg.sender === 'user' ? '#DCF8C6' : '#E6E6E6'
            }}
          >
            <strong>{msg.sender === 'user' ? 'You' : 'AI'}:</strong><br />
            {msg.text}
          </div>
        ))}

        {loading && (
          <div
            style={{
              ...styles.message,
              alignSelf: 'flex-start',
              fontStyle: 'italic',
              color: '#666'
            }}
          >
            AI is typing...
          </div>
        )}
      </div>

      <div style={styles.inputArea}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          style={styles.input}
          placeholder="Type a message..."
        />
        <button onClick={sendMessage} style={styles.button}>Send</button>
      </div>
    </div>
  );
};

const styles = {
  container: {
    padding: 20,
    fontFamily: 'Arial',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column'
  },
  chatBox: {
    flex: 1,
    padding: 10,
    border: '1px solid #ccc',
    borderRadius: 10,
    overflowY: 'auto',
    marginBottom: 10,
    display: 'flex',
    flexDirection: 'column',
    gap: 10
  },
  inputArea: {
    display: 'flex',
    gap: 10
  },
  input: {
    flex: 1,
    padding: 10,
    borderRadius: 5,
    border: '1px solid #ccc'
  },
  button: {
    padding: '10px 20px',
    borderRadius: 5,
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none'
  },
  message: {
    padding: 10,
    borderRadius: 10,
    maxWidth: '80%',
    whiteSpace: 'pre-wrap'
  },
  clearButton: {
    alignSelf: 'center',
    marginBottom: 10,
    padding: '6px 12px',
    backgroundColor: '#e53935',
    color: 'white',
    border: 'none',
    borderRadius: 5,
    cursor: 'pointer'
  }
};

export default Chat;
